const request     = require('supertest');
const expect      = require('chai').expect;
const server      = require('./lib/express');
const {token, orgId,brandfolderId} = require('../lib/env');


describe('List Tags', function () {

  it('Lists all tags', function(done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'list_tags',
          moduleParam: {
          },
          registrationData: {
            token: token,
            orgId: orgId,
            brandfolderId:brandfolderId,
          }        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function(err, res) {
        expect(res.body).to.have.property('status').to.equal(0);
        expect(res.body).to.have.property('value');
        done();
      });
  })

  
});
